mapper.update:
update dailyReport
set exerciseId = #{exerciseId},
exerciseAccuracy = #{exerciseAccuracy}
where userId = #{userId}
and
reportDate like #{reportDate}

mapper.select:
select *
from dailyReport
where 
userId = #{userId}
and
reportDate like #{reportDate}

select exerciseId, exerciseAccuracy
from dailyReport
where 
userId = #{userId}
and
reportDate like #{reportDate}

select count(*)
from dailyReport
where userId = #{userId}
and
reportDate like #{reportDate}
